INSERT INTO `categories` (`id`, `title`, `short_description`, `created_at`, `updated_at`) VALUES
(1, 'Техника для кухни', 'В разделе «Техника для кухни» интернет магазина «Электронный мир» представлен широкий выбор бытовой техники, способной оказать неоценимую помощь в процессе приготовления пищи. Здесь Вы можете купить аэрогрили, блендеры, бутербродницы, грили, блинницы, барбекю, измельчители, йогуртницы, кофеварки, кофемолки, кухонные комбайны, микроволновые печи, миксеры, мороженицы, мультиварки, мясорубки, пароварки, резки, соковыжималки, сушки для фруктов и овощей, тостеры, фритюрницы, хлебопечи, чайники и электропечи. В разделе «Техника для кухни» представлена надежная и качественная техника от известных мировых производителей.\r\nПодробнее: http://elmir.ua/kitchen_appliances/', '2017-06-01 06:12:43', NULL),
(2, 'Техника для дома', 'Бытовая техника, представленная в разделе «Техника для дома» нашего интернет магазина, включает в себя большой ассортимент бытовых весов, утюгов, швейных машин, пылесосов, а также аксессуаров к ним. Ознакомиться с техническими характеристиками устройств, их подробным описанием, динамикой цены за последний период времени, а также прочесть отзывы на интересующие модели - все это можно сделать в разделе «Техника для дома». Интернет магазин имеет преимущество перед традиционным салоном техники в том, что Вы можете ознакомиться со всей необходимой информацией не выходя из дома или даже на экране своего мобильного телефона.\r\nПодробнее: http://elmir.ua/household/', '2017-06-02 03:45:21', NULL),
(3, 'Видео техника', 'Это потрясающий инструмент, который вам поможет трансформировать любой стресс, любую эмоцию и принесет большую пользу и вашему телу, и вашему уму. И, я надеюсь, понравится вам так же, как нравится мне. qwwe', '2017-06-03 02:30:40', '2017-06-19 18:10:55'),
(4, 'Смартфоны', 'Смартфо́н (англ. smartphone — умный телефон) — мобильный телефон, дополненный функциональностью карманного персонального компьютера.\r\n\r\nТакже коммуникатор (англ. communicator, PDA phone) — карманный персональный компьютер, дополненный функциональностью мобильного телефона', '2017-06-04 15:28:43', NULL),
(5, 'Компьютеры и ноутбуки', 'Ноутбук (англ. notebook — блокнот, портативный ПК) — переносной персональный компьютер, в корпусе которого объединены типичные компоненты ПК, включая дисплей, клавиатуру и устройство указания (обычно сенсорная панель, или тачпад), а также аккумуляторные батареи. Ноутбуки отличаются небольшими размерами и весом, время автономной работы ноутбуков изменяется в пределах от 2 до 15 часов.123', '2017-06-05 08:14:31', '2017-06-20 02:41:38');

